mapper.update:

UPDATE T_OTT_BOARD
	SET BOARD_VIEWCOUNT = BOARD_VIEWCOUNT + 1
	WHERE BOARD_SEQ = #{boardSeq}


UPDATE T_OTT_BOARD
	SET BOARD_SEQ = BOARD_SEQ - 1
	WHERE BOARD_SEQ > #{boardSeq}

mapper.insert:

INSERT INTO T_OTT_IMG(
	BOARD_SEQ,
	IMG_SEQ,
	BOARD_CATEGORY,
	IMG_FILE
) VALUES


(
	#{boardFile.boardSeq},
	(
		SELECT IFNULL(MAX(A.IMG_SEQ), 0) + 1
			FROM T_OTT_IMG A
			WHERE A.BOARD_SEQ = #{boardFile.boardSeq}
	),
	#{boardFile.boardCategory},
	#{boardFile.imgFile}
)
