mapper.select:

SELECT *
	FROM T_OTT_BOARD
	WHERE BOARD_SEQ = #{boardSeq}
	  AND BOARD_CATEGORY = #{boardCategory}

mapper.update:

UPDATE T_OTT_BOARD
	SET BOARD_VIEWCOUNT = BOARD_VIEWCOUNT + 1
	WHERE BOARD_SEQ = #{boardSeq}
	  AND BOARD_CATEGORY = #{boardCategory}


UPDATE T_OTT_BOARD
	SET BOARD_SEQ = BOARD_SEQ - 1
	WHERE BOARD_SEQ > #{boardSeq}

mapper.select:

SELECT IFNULL(MAX(BOARD_SEQ), 0) + 1
	FROM T_OTT_BOARD
	WHERE BOARD_CATEGORY = #{boardCategory}

mapper.insert:

INSERT INTO T_OTT_IMG(
	BOARD_SEQ,
	IMG_SEQ,
	BOARD_CATEGORY,
	IMG_FILE
) VALUES



(
	#{aaa.boardSeq},
	(
		SELECT IFNULL(MAX(A.IMG_SEQ), 0) + 1
			FROM T_OTT_IMG A
			WHERE A.BOARD_SEQ = #{aaa.boardSeq}
	),
	#{aaa.boardCategory},
	#{aaa.imgFile}
)

mapper.select:
SELECT *
	FROM T_OTT_IMG
	WHERE BOARD_SEQ = #{boardSeq}
	  AND BOARD_CATEGORY = #{boardCategory}
